step.script_example:

() => {
  return {
    headline: document.querySelector('h1')?.textContent,
    subheadline: document.querySelector('.hero p')?.textContent,
    cta: document.querySelector('.cta-button')?.textContent,
    benefits: Array.from(document.querySelectorAll('.benefit')).map(el => el.textContent)
  }
}

data_extraction_scripts.script:

() => {
  const conversionData = document.querySelector('.conversion-rate');
  const clickData = document.querySelector('.click-through-rate');
  return {
    conversion: conversionData?.textContent,
    clickThrough: clickData?.textContent,
    timestamp: new Date().toISOString()
  }
}

metric_extraction.script:

() => {
  return {
    pageViews: document.querySelector('[data-metric="page-views"]')?.textContent,
    conversionRate: document.querySelector('[data-metric="conversion-rate"]')?.textContent,
    bounceRate: document.querySelector('[data-metric="bounce-rate"]')?.textContent,
    avgTimeOnPage: document.querySelector('[data-metric="avg-time"]')?.textContent,
    clickThroughRate: document.querySelector('[data-metric="ctr"]')?.textContent,
    costPerAcquisition: document.querySelector('[data-metric="cpa"]')?.textContent,
    timestamp: new Date().toISOString()
  }
}
